import React, { useState } from 'react';
import {
  Button,
  TextField,
  Paper,
  Typography,
  makeStyles,
  IconButton,
  Grid,
} from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';

const useStyles = makeStyles((theme) => ({
  chatContainer: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
    width: 300,
    backgroundColor: theme.palette.background.paper,
    borderRadius: 8,
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    zIndex: 1000,
  },
  chatHeader: {
    padding: theme.spacing(1),
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.text.primary,
    textAlign: 'center',
    fontFamily: "'Ancizar Serif', serif",
  },
  messages: {
    padding: theme.spacing(2),
    maxHeight: 200,
    overflowY: 'auto',
  },
  message: {
    marginBottom: theme.spacing(1),
    padding: theme.spacing(1),
    backgroundColor: '#1e1e1e',
    borderRadius: 4,
  },
  inputContainer: {
    padding: theme.spacing(1),
    display: 'flex',
    alignItems: 'center',
  },
  input: {
    flexGrow: 1,
    marginRight: theme.spacing(1),
  },
  buttonContainer: {
    padding: theme.spacing(1),
    backgroundColor: theme.palette.background.paper,
  },
  optionButton: {
    margin: theme.spacing(0.5),
    fontSize: '0.8rem',
    textTransform: 'none',
    fontFamily: "'Ancizar Serif', serif",
  },
}));

// Готові відповіді для типових проблем
const predefinedResponses = {
  'Проблема з доставкою': 'Перевірте статус вашого замовлення в особистому кабінеті. Якщо проблема не вирішена, напишіть нам номер замовлення, і ми допоможемо!',
  'Питання про товар': 'Будь ласка, уточніть, який товар вас цікавить, і ми надамо всю необхідну інформацію.',
  'Інше': 'Опишіть вашу проблему детальніше, і ми постараємося допомогти якомога швидше!',
};

export default function SupportChat() {
  const classes = useStyles();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user', time: new Date().toLocaleTimeString() }]);
      setInput('');
      // Автовідповідь
      setTimeout(() => {
        setMessages(prev => [...prev, { text: 'Дякуємо за звернення! Ми скоро відповімо.', sender: 'support', time: new Date().toLocaleTimeString() }]);
      }, 1000);
    }
  };

  const handleOptionClick = (option) => {
    setMessages([...messages, { text: option, sender: 'user', time: new Date().toLocaleTimeString() }]);
    // Додаємо готову відповідь
    setTimeout(() => {
      setMessages(prev => [...prev, { text: predefinedResponses[option], sender: 'support', time: new Date().toLocaleTimeString() }]);
    }, 1000);
  };

  return (
    <Paper className={classes.chatContainer}>
      <Typography variant="h6" className={classes.chatHeader}>
        Технічна підтримка
      </Typography>
      <div className={classes.messages}>
        {messages.map((msg, index) => (
          <div key={index} className={classes.message}>
            <Typography variant="body2">{msg.text}</Typography>
            <Typography variant="caption" color="textSecondary">{msg.sender === 'user' ? 'Ви' : 'Підтримка'} - {msg.time}</Typography>
          </div>
        ))}
      </div>
      <div className={classes.inputContainer}>
        <TextField
          className={classes.input}
          variant="outlined"
          size="small"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Напишіть повідомлення..."
        />
        <IconButton color="primary" onClick={handleSend} disabled={!input.trim()}>
          <SendIcon />
        </IconButton>
      </div>
      <div className={classes.buttonContainer}>
        <Grid container spacing={1}>
          {Object.keys(predefinedResponses).map((option) => (
            <Grid item key={option}>
              <Button
                variant="outlined"
                color="primary"
                size="small"
                className={classes.optionButton}
                onClick={() => handleOptionClick(option)}
              >
                {option}
              </Button>
            </Grid>
          ))}
        </Grid>
      </div>
    </Paper>
  );
}
