import React, { useState } from 'react';
import {
  Button,
  Paper,
  Typography,
  makeStyles,
  Grid,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  chatContainer: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
    width: 300,
    backgroundColor: theme.palette.background.paper,
    borderRadius: 8,
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    zIndex: 1000,
  },
  chatHeader: {
    padding: theme.spacing(1),
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.text.primary,
    textAlign: 'center',
    fontFamily: "'Ancizar Serif', serif",
  },
  messages: {
    padding: theme.spacing(2),
    maxHeight: 200,
    overflowY: 'auto',
  },
  message: {
    marginBottom: theme.spacing(1),
    padding: theme.spacing(1),
    backgroundColor: '#1e1e1e',
    borderRadius: 4,
  },
  buttonContainer: {
    padding: theme.spacing(1),
    backgroundColor: theme.palette.background.paper,
  },
  optionButton: {
    margin: theme.spacing(0.5),
    fontSize: '0.8rem',
    textTransform: 'none',
    fontFamily: "'Ancizar Serif', serif",
    backgroundColor: '#4a90e2', // Світло-синій фон для контрасту з #1e1e1e
    color: '#ffffff', // Білий текст
    '&:hover': {
      backgroundColor: '#357bd8', // Темніший відтінок при наведенні
    },
  },
}));

// Готові відповіді для типових проблем
const predefinedResponses = {
  'Проблема з доставкою': 'Перевірте статус вашого замовлення в особистому кабінеті. Якщо проблема не вирішена, напишіть нам номер замовлення, і ми допоможемо!',
  'Питання про товар': 'Будь ласка, уточніть, який товар вас цікавить, і ми надамо всю необхідну інформацію.',
  'Інше': 'Опишіть вашу проблему детальніше, і ми постараємося допомогти якомога швидше!',
};

export default function SupportChat() {
  const classes = useStyles();
  const [messages, setMessages] = useState([]);

  const handleOptionClick = (option) => {
    setMessages([...messages, { text: option, sender: 'user', time: new Date().toLocaleTimeString() }]);
    // Додаємо готову відповідь
    setTimeout(() => {
      setMessages(prev => [...prev, { text: predefinedResponses[option], sender: 'support', time: new Date().toLocaleTimeString() }]);
    }, 1000);
  };

  return (
    <Paper className={classes.chatContainer}>
      <Typography variant="h6" className={classes.chatHeader}>
        Технічна підтримка
      </Typography>
      <div className={classes.messages}>
        {messages.map((msg, index) => (
          <div key={index} className={classes.message}>
            <Typography variant="body2" style={{ color: '#ffffff' }}>{msg.text}</Typography>
            <Typography variant="caption" style={{ color: '#bbbbbb' }}>{msg.sender === 'user' ? 'Ви' : 'Підтримка'} - {msg.time}</Typography>
          </div>
        ))}
      </div>
      <div className={classes.buttonContainer}>
        <Grid container spacing={1}>
          {Object.keys(predefinedResponses).map((option) => (
            <Grid item key={option}>
              <Button
                variant="contained"
                size="small"
                className={classes.optionButton}
                onClick={() => handleOptionClick(option)}
              >
                {option}
              </Button>
            </Grid>
          ))}
        </Grid>
      </div>
    </Paper>
  );
}
