import React, { useEffect, useState, useReducer } from 'react';
import { createMuiTheme, ThemeProvider } from '@material-ui/core';
import { Routes, Route } from 'react-router-dom';
import { CartContext, ProductsContext, CurrencyContext } from './services/context'; // додай CurrencyContext
import changeQuantity from './services/changeQuantity';

export default function AppRoutes() {
  const [productData, setProductData] = useState([]);
  const [currencyData, setCurrencyData] = useState([]); // додано курс валют

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch(
          "https://v1-sneakers.p.rapidapi.com/v1/sneakers?" +
          new URLSearchParams({ limit: "100", releaseDate: "2019-11-01" }),
          {
            headers: {
              "x-rapidapi-host": "v1-sneakers.p.rapidapi.com",
              "x-rapidapi-key": "ff08e7f7fcmsh6260d8fb0f6c656p1d6c04jsndd1e2becebf3",
            },
          }
        );
        const { results } = await response.json();
        setProductData(results);
      } catch (err) {
        console.log("NETWORK ERROR");
        setProductData([]);
      }
    }

    async function fetchCurrency() {
      try {
        const res = await fetch("https://api.privatbank.ua/p24api/pubinfo?json&exchange&coursid=5");
        const data = await res.json();
        setCurrencyData(data); // це масив з курсами валют
      } catch (e) {
        console.log("Currency fetch error");
        setCurrencyData([]);
      }
    }

    fetchData();
    fetchCurrency();
  }, []);

  const theme = createMuiTheme({
    palette: {
      type: "dark",
      primary: { main: "#37474F" },
      secondary: { main: "#8E244D" },
      background: { default: "#121212", paper: "#1E1E1E" },
      text: { primary: "#E0E0E0", secondary: "#9E9E9E" },
    },
    typography: { fontFamily: "'Montserrat', 'Roboto', 'Open Sans', sans-serif" },
  });

  return (
    <CartContext.Provider value={useReducer(changeQuantity, { total: 0 })}>
      <ProductsContext.Provider value={[productData, setProductData]}>
        <CurrencyContext.Provider value={currencyData}>
          <ThemeProvider theme={theme}>
            <Routes>
              {/* ...твої маршрути */}
            </Routes>
          </ThemeProvider>
        </CurrencyContext.Provider>
      </ProductsContext.Provider>
    </CartContext.Provider>
  );
}
