import React from 'react';
import { 
  Button, 
  Typography, 
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TextField,
  Paper,
} from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { ref, update, remove } from '../services/firebase';

const ReviewsTable = ({
  classes,
  reviews,
  setReviews,
  itemsPerPage,
  currentPage,
  setCurrentPage,
}) => {
  const [deleteDialogOpen, setDeleteDialogOpen] = React.useState(false);
  const [editDialogOpen, setEditDialogOpen] = React.useState(false);
  const [reviewToDelete, setReviewToDelete] = React.useState(null);
  const [reviewToEdit, setReviewToEdit] = React.useState(null);
  const [editedReview, setEditedReview] = React.useState({});

  const totalPages = Math.min(3, Math.ceil(reviews.length / itemsPerPage));
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const displayedReviews = reviews.slice(startIndex, endIndex);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleDeleteOpen = (reviewId) => {
    setReviewToDelete(reviewId);
    setDeleteDialogOpen(true);
  };

  const handleDeleteClose = () => {
    setDeleteDialogOpen(false);
    setReviewToDelete(null);
  };

  const handleDeleteConfirm = async () => {
    if (reviewToDelete) {
      try {
        const reviewRef = ref(database, `reviews/${reviewToDelete}`);
        await remove(reviewRef);
        setReviews(reviews.filter((review) => review.id !== reviewToDelete));
      } catch (error) {
        console.error('Помилка видалення відгуку:', error.message);
        alert('Помилка при видаленні відгуку: ' + error.message);
      }
    }
    handleDeleteClose();
  };

  const handleEditOpen = (review) => {
    setReviewToEdit(review.id);
    setEditedReview({
      id: review.id,
      text: review.text,
      rating: review.rating,
      userName: review.userName,
    });
    setEditDialogOpen(true);
  };

  const handleEditClose = () => {
    setEditDialogOpen(false);
    setReviewToEdit(null);
    setEditedReview({});
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedReview((prev) => ({ ...prev, [name]: value }));
  };

  const handleSaveEdit = async () => {
    if (reviewToEdit) {
      try {
        const reviewRef = ref(database, `reviews/${reviewToEdit}`);
        await update(reviewRef, {
          text: editedReview.text,
          rating: parseInt(editedReview.rating) || 0,
          userName: editedReview.userName,
        });
        setReviews((prevReviews) =>
          prevReviews.map((review) =>
            review.id === reviewToEdit ? { ...review, ...editedReview } : review
          )
        );
        alert('Відгук успішно оновлено!');
      } catch (error) {
        console.error('Помилка редагування відгуку:', error.message);
        alert('Помилка при редагуванні відгуку: ' + error.message);
      }
    }
    handleEditClose();
  };

  return (
    <Box className={classes.contentSection}>
      <Typography variant="h5" className={classes.adminText} style={{ marginBottom: '25px' }}>
        Список відгуків
      </Typography>
      {reviews.length > 0 ? (
        <>
          <TableContainer className={classes.tableContainer} component={Paper}>
            <Table className={classes.table} aria-label="reviews table">
              <TableHead className={classes.tableHead}>
                <TableRow>
                  <TableCell>ID</TableCell>
                  <TableCell>Текст відгуку</TableCell>
                  <TableCell>Оцінка</TableCell>
                  <TableCell>Ім'я користувача</TableCell>
                  <TableCell>Дії</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {displayedReviews.map((review) => (
                  <TableRow key={review.id} className={classes.tableRow}>
                    <TableCell className={classes.tableCell}>{review.id}</TableCell>
                    <TableCell className={classes.tableCell}>{review.text}</TableCell>
                    <TableCell className={classes.tableCell}>{review.rating}</TableCell>
                    <TableCell className={classes.tableCell}>{review.userName}</TableCell>
                    <TableCell className={classes.tableCell}>
                      <IconButton
                        className={classes.editButton}
                        onClick={() => handleEditOpen(review)}
                        aria-label="edit"
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        className={classes.deleteButton}
                        onClick={() => handleDeleteOpen(review.id)}
                        aria-label="delete"
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          <Box className={classes.pagination}>
            {[1, 2, 3].slice(0, totalPages).map((page) => (
              <Button
                key={page}
                onClick={() => handlePageChange(page)}
                className={`${classes.pageButton} ${currentPage === page ? classes.activePageButton : ''}`}
              >
                {page}
              </Button>
            ))}
          </Box>
        </>
      ) : (
        <Typography className={classes.noOrdersText}>
          Відгуки відсутні.
        </Typography>
      )}

      {/* Діалог видалення відгуку */}
      <Dialog
        open={deleteDialogOpen}
        onClose={handleDeleteClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">Підтвердження видалення</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Ви впевнені, що хочете видалити цей відгук? Ця дія є незворотною.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteClose} color="primary">
            Скасувати
          </Button>
          <Button onClick={handleDeleteConfirm} color="secondary" autoFocus>
            Видалити
          </Button>
        </DialogActions>
      </Dialog>

      {/* Діалог редагування відгуку */}
      <Dialog
        open={editDialogOpen}
        onClose={handleEditClose}
        aria-labelledby="edit-dialog-title"
        maxWidth="md"
        fullWidth
      >
        <DialogTitle id="edit-dialog-title">Редагування відгуку</DialogTitle>
        <DialogContent className={classes.dialogContent}>
          <TextField
            label="Текст відгуку"
            name="text"
            value={editedReview.text || ''}
            onChange={handleInputChange}
            fullWidth
            multiline
            rows={3}
          />
          <TextField
            label="Оцінка (1-5)"
            name="rating"
            type="number"
            value={editedReview.rating || ''}
            onChange={handleInputChange}
            fullWidth
            inputProps={{ min: 1, max: 5 }}
          />
          <TextField
            label="Ім'я користувача"
            name="userName"
            value={editedReview.userName || ''}
            onChange={handleInputChange}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose} color="#631935">
            Скасувати
          </Button>
          <Button onClick={handleSaveEdit} color="#631935">
            Зберегти
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ReviewsTable;
