import { Routes, Route } from 'react-router-dom';
import NotFound from './components/NotFound'
import Home from './components/Home'
import About from './components/About'
import Cart from './components/Cart'
import Layout from './components/Layout'
import { CartContext, ProductsContext } from './services/context';
import { useEffect, useReducer, useState, createContext } from 'react';
import changeQuantity from './services/changeQuantity';
import { createMuiTheme, ThemeProvider } from '@material-ui/core';
import ProdPagination from './components/ProdPagination';
import ProductsDetails from './components/ProductDetails';

// Створюємо контекст для валют
export const CurrencyContext = createContext();

export default function AppRoutes() {
  const [productData, setProductData] = useState([]);
  const [currencyData, setCurrencyData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch("https://v1-sneakers.p.rapidapi.com/v1/sneakers?" + new URLSearchParams({ 'limit': '100', 'releaseDate': '2019-11-01' }), {
          headers: {
            'x-rapidapi-host': 'v1-sneakers.p.rapidapi.com',
            'x-rapidapi-key': 'ff08e7f7fcmsh6260d8fb0f6c656p1d6c04jsndd1e2becebf3'
          }
        });
        const { results } = await response.json();
        setProductData(results);
      } catch (err) {
        console.log('NETWORK ERROR');
        setProductData([]);
      }
    };

    async function fetchCurrency() {
      try {
        const response = await fetch('https://bank.gov.ua/NBUStatService/v1/statdirectory/exchangenew?json');
        const data = await response.json();
        setCurrencyData(data);
      } catch (err) {
        console.log('Currency fetch error', err);
        setCurrencyData([]);
      }
    }

    fetchData();
    fetchCurrency();
  }, []);

  const theme = createMuiTheme({
    palette: {
      type: 'dark',
      primary: { main: '#37474F' },
      secondary: { main: '#8E244D' },
      background: { default: '#121212', paper: '#1E1E1E' },
      text: { primary: '#E0E0E0', secondary: '#9E9E9E' },
    },
    typography: {
      fontFamily: "'Montserrat', 'Roboto', 'Open Sans', sans-serif",
    },
  });

  return (
    <CartContext.Provider value={useReducer(changeQuantity, { total: 0 })}>
      <ProductsContext.Provider value={[productData, setProductData]}>
        <CurrencyContext.Provider value={currencyData}>
          <ThemeProvider theme={theme}>
            <Routes>
              <Route path="/" element={<Layout />}>
                <Route path="/" element={<Home />} />
                <Route path="products" element={<ProdPagination />} />
                <Route path="/products/:productID" element={<ProductsDetails />} />
                <Route path="cart" element={<Cart />} />
                <Route path="about" element={<About />} />
                <Route path="*" element={<NotFound />} />
              </Route>
            </Routes>
          </ThemeProvider>
        </CurrencyContext.Provider>
      </ProductsContext.Provider>
    </CartContext.Provider>
  );
}
