import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../services/firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { Button, Typography } from '@material-ui/core';

export default function AdminPage() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        navigate('/'); // Перенаправлення на головну, якщо не авторизовано
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/'); // Перенаправлення на головну після виходу
    } catch (error) {
      console.error('Помилка виходу:', error.message);
    }
  };

  if (!user) {
    return <div>Завантаження...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <Typography variant="h4">Панель адміністратора</Typography>
      <Typography variant="subtitle1">Вітаємо, {user.email}!</Typography>
      <Button variant="contained" color="secondary" onClick={handleLogout} style={{ marginTop: '20px' }}>
        Вийти
      </Button>
      <p>Тут буде контент для адміністратора.</p>
    </div>
  );
}
