import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../services/firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { 
  Button, 
  Typography, 
  Container, 
  Paper, 
  makeStyles, 
  Box 
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.grey[900], // Темний фон
    minHeight: '100vh',
    padding: theme.spacing(4, 0),
    color: theme.palette.text.primary,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  header: {
    fontFamily: "'Lavishly Yours', cursive",
    fontSize: '3.5rem',
    color: theme.palette.primary.main,
    textShadow: '0 0 10px rgba(255, 255, 255, 0.3)',
    textAlign: 'center',
    marginBottom: theme.spacing(2),
  },
  subheader: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.5rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
    marginBottom: theme.spacing(4),
  },
  adminCard: {
    backgroundColor: theme.palette.primary.dark,
    padding: theme.spacing(3),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '600px',
    marginBottom: theme.spacing(4),
  },
  adminText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.2rem',
    color: theme.palette.text.primary,
  },
  logoutButton: {
    marginTop: theme.spacing(2),
    fontFamily: "'Ancizar Serif', serif",
  },
  contentSection: {
    backgroundColor: theme.palette.grey[800],
    padding: theme.spacing(3),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '1200px',
  },
  placeholderText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.2rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
  },
}));

export default function AdminPage() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const classes = useStyles();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        navigate('/'); // Перенаправлення на головну, якщо не авторизовано
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/'); // Перенаправлення на головну після виходу
    } catch (error) {
      console.error('Помилка виходу:', error.message);
    }
  };

  if (!user) {
    return <div style={{ color: 'white', textAlign: 'center', padding: '20px' }}>Завантаження...</div>;
  }

  return (
    <div className={classes.root}>
      <Container>
        <Typography variant="h1" className={classes.header}>
          Панель адміністратора
        </Typography>
        <Typography className={classes.subheader}>
          Вітаємо, {user.email}!
        </Typography>

        <Paper className={classes.adminCard}>
          <Typography className={classes.adminText}>
            Ви увійшли як: <strong>{user.email}</strong>
          </Typography>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleLogout}
            className={classes.logoutButton}
          >
            Вийти
          </Button>
        </Paper>

        <Box className={classes.contentSection}>
          <Typography className={classes.placeholderText}>
            Тут буде панель управління (таблиця товарів, форма додавання тощо).
          </Typography>
        </Box>
      </Container>
    </div>
  );
}
