import { Card, CardActionArea, CardContent, CardMedia, Container, Divider, List, ListItem, ListItemText, makeStyles, Paper, Typography } from '@material-ui/core';
import React from 'react';
import Link from '@material-ui/core/Link';
import Slider from './Slider';

const useStyles = makeStyles(theme => ({

  title: {
    backgroundColor: theme.palette.secondary.main,
    color: 'white',
    padding: '5px 20px',
    margin: '0px 0px 25px',
  },

  card: {
    backgroundColor: theme.palette.primary.main,
    color: 'white',
    width: 205,
    height: 250,
    margin: '10px',
    display: 'inline-block',
  },

  container: {
    padding: '20px',
  },

  paper: {
    paddingBottom: '30px',
  },

  media: {
    height: 140,
  },

  content: {
    fontFamily: 'Yusei Magic,sans-serif',
    marginTop: '40px',
    paddingLeft: '30px',
  }
}))

const cards = [
  { title: 'Telegram', value: '+380685325163' },
  { title: 'Email', value: 'ruslanborsuk90@gmail.com', link: 'mail.google.com/mail/u/0/?fs=1&tf=cm&to=ruslanborsuk90@gmail.com&su=Спілкування+за+взуття&body=Повідомлення' },
  { title: 'Github', value: 'https://github.com/\nFlorenceses', link: 'https://github.com/Florenceses' },
  { title: 'Facebook', value: 'https://www.facebook.com\n/ruslan.borsuk', link: 'https://www.facebook.com/profile.php?id=100022394320096' },
  { title: 'Instagram', value: 'https://www.instagram.com/\nflorences.__', link: 'https://www.instagram.com/florences.__/'}
]
export default function About() {

  const classes = useStyles();

  return (
    <div>
      <Container className={classes.container} >
        <Paper >
          <Typography variant='h3' className={classes.title}>Про додаток</Typography>
          <Typography variant='h6' className={classes.content}>Shoe Store — це веб-додаток на основі Reactjs як інтерфейсу з реакційним маршрутизатором 6beta та materialui, який розгортається під час сплеску. </Typography>
          <Typography variant='h6' className={classes.content}>Функціональні можливості включають.. </Typography>
          <div >
            <List component="nav" aria-label="app functionality">
              <ListItem>
                <ListItemText primary="1. Перерахування продуктів із нумерацією сторінок" />
              </ListItem>
              <Divider />

              <ListItem>
                <ListItemText primary="2. Відображення деталей продукту за допомогою динамічних маршрутів" />
              </ListItem>
              <Divider />

              <ListItem>
                <ListItemText primary="3. Опція додавання в кошик з різних розділів програми" />
              </ListItem>
              <Divider />

              <ListItem>
                <ListItemText primary="4. Зроблений на замовлення слайдер для відображення різних елементів в обмеженому просторі" />
              </ListItem>
              <Divider />

              <ListItem>
                <ListItemText primary="5. Відображення нового надходження на головній сторінці" />
              </ListItem>
              <Divider />

              <ListItem>
                <ListItemText primary="6. Повністю чуйний на всіх розмірах екрана" />
              </ListItem>
              <Divider />
            </List>
          </div>
        </Paper>
      </Container>

      <Container className={classes.container} >
        <Paper
          className={classes.paper} >
          <Typography variant='h3' className={classes.title}>Зв'язок з Florence @</Typography>

          <Slider itemHeight={250} itemWidth={225} itemsTotal={5} itemsDisplayed={4}>
            {cards.map((card) => {
              console.log('ABOUT this is card.title.toLocaleLowerCase() = ', card.title.toLocaleLowerCase())
              return (
                <Link href={card.link} target="_blank" rel="noopener" key={card.title} >
                  <Card className={classes.card}>
                    <CardActionArea>
                      <CardMedia
                        className={classes.media}
                        image={`/u${card.title.toLocaleLowerCase()}.png`}
                        // image={`public/icons/u${card.title.toLocaleLowerCase()}.png`}
                        title={card.title}
                      />
                      <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                          {card.title}
                        </Typography>
                        <Typography variant="body2" color="white" component="p">
                          {card.value}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Link>
              )
            })}
          </Slider>

        </Paper>
      </Container>
    </div >
  );
}
