import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, database } from '../services/firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { ref, onValue } from 'firebase/database';
import { 
  Button, 
  Typography, 
  Container, 
  Paper, 
  makeStyles, 
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.grey[900],
    minHeight: '100vh',
    padding: theme.spacing(4, 0),
    color: theme.palette.text.primary,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  header: {
    fontFamily: "'Lavishly Yours', cursive",
    fontSize: '3.5rem',
    color: theme.palette.primary.main,
    textShadow: '0 0 10px rgba(255, 255, 255, 0.3)',
    textAlign: 'center',
    marginBottom: theme.spacing(2),
  },
  subheader: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.5rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
    marginBottom: theme.spacing(4),
  },
  adminCard: {
    backgroundColor: theme.palette.primary.dark,
    padding: theme.spacing(3),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '600px',
    marginBottom: theme.spacing(4),
  },
  adminText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.2rem',
    color: theme.palette.text.primary,
  },
  logoutButton: {
    marginTop: theme.spacing(2),
    fontFamily: "'Ancizar Serif', serif",
  },
  contentSection: {
    backgroundColor: theme.palette.grey[800],
    padding: theme.spacing(3),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '1280px',
  },
  table: {
    minWidth: 700,
  },
  tableHead: {
    backgroundColor: theme.palette.primary.main,
    '& th': {
      color: theme.palette.common.white,
      fontFamily: "'Ancizar Serif', serif",
      fontWeight: 'bold',
    },
  },
  tableRow: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
  },
  tableCell: {
    fontFamily: "'Ancizar Serif', serif",
    color: theme.palette.text.primary,
    width: "200px",
  },
  noOrdersText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.2rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
    padding: theme.spacing(3),
  },
}));

export default function AdminPage() {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();
  const classes = useStyles();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        navigate('/');
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  useEffect(() => {
    const ordersRef = ref(database, 'orders');
    const unsubscribe = onValue(ordersRef, (snapshot) => {
      const data = snapshot.val();
      console.log('Дані з Firebase:', data); // Дебагування
      if (data) {
        const ordersList = Object.entries(data).map(([id, order]) => {
          // Перевірка, чи є поле items (масив товарів)
          const firstItem = Array.isArray(order.items) && order.items.length > 0 ? order.items[0] : {};
          return {
            id,
            name: firstItem.name || order.name || 'Невідомо', // Спроба отримати name із items або напряму
            price: firstItem.price || order.price || 0,
            quantity: firstItem.quantity || order.quantity || 0,
            orderDate: order.orderDate || new Date().toISOString(),
            totalPrice: order.totalPrice || 0,
            fullName: order.user?.fullName || 'Невідомо',
            phone: order.user?.phone || 'Невідомо',
            email: order.user?.email || 'Невідомо',
            deliveryMethod: order.user?.deliveryMethod || 'Невідомо',
            address: `${order.user?.address || ''}, ${order.user?.city || ''}, ${order.user?.district || ''}, ${order.user?.country || ''}`
              .trim()
              .replace(/, ,/g, ',')
              .replace(/^,|,$/g, '') || 'Невідомо',
          };
        });
        setOrders(ordersList);
      } else {
        setOrders([]);
      }
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/');
    } catch (error) {
      console.error('Помилка виходу:', error.message);
    }
  };

  if (!user) {
    return <div style={{ color: 'white', textAlign: 'center', padding: '20px' }}>Завантаження...</div>;
  }

  return (
    <div className={classes.root}>
      <Container>
        <Typography variant="h1" className={classes.header}>
          Панель адміністратора
        </Typography>
        <Typography className={classes.subheader}>
          Вітаємо, {user.email}!
        </Typography>

        <Paper className={classes.adminCard}>
          <Typography className={classes.adminText}>
            Ви увійшли як: <strong>{user.email}</strong>
          </Typography>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleLogout}
            className={classes.logoutButton}
          >
            Вийти
          </Button>
        </Paper>

        <Box className={classes.contentSection}>
          <Typography variant="h5" className={classes.adminText} style={{ marginBottom: '20px' }}>
            Список замовлень
          </Typography>
          {orders.length > 0 ? (
            <TableContainer component={Paper}>
              <Table className={classes.table} aria-label="orders table">
                <TableHead className={classes.tableHead}>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Назва товару</TableCell>
                    <TableCell>Ціна</TableCell>
                    <TableCell>Кількість</TableCell>
                    <TableCell>Дата замовлення</TableCell>
                    <TableCell>Сума (грн)</TableCell>
                    <TableCell>Ім'я</TableCell>
                    <TableCell>Телефон</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Доставка</TableCell>
                    <TableCell>Адреса</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {orders.map((order) => (
                    <TableRow key={order.id} className={classes.tableRow}>
                      <TableCell className={classes.tableCell}>{order.id}</TableCell>
                      <TableCell className={classes.tableCell}>{order.name}</TableCell>
                      <TableCell className={classes.tableCell}>{order.price}</TableCell>
                      <TableCell className={classes.tableCell}>{order.quantity}</TableCell>
                      <TableCell className={classes.tableCell}>
                        {new Date(order.orderDate).toLocaleString()}
                      </TableCell>
                      <TableCell className={classes.tableCell}>{order.totalPrice}</TableCell>
                      <TableCell className={classes.tableCell}>{order.fullName}</TableCell>
                      <TableCell className={classes.tableCell}>{order.phone}</TableCell>
                      <TableCell className={classes.tableCell}>{order.email}</TableCell>
                      <TableCell className={classes.tableCell}>{order.deliveryMethod}</TableCell>
                      <TableCell className={classes.tableCell}>{order.address}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          ) : (
            <Typography className={classes.noOrdersText}>
              Замовлення відсутні.
            </Typography>
          )}
        </Box>
      </Container>
    </div>
  );
}
