import React, { useState } from 'react';
import {
  Button,
  TextField,
  Paper,
  Typography,
  makeStyles,
  IconButton,
} from '@material-ui/core';
import SendIcon from '@material-ui/icons/Send';

const useStyles = makeStyles((theme) => ({
  chatContainer: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
    width: 300,
    backgroundColor: theme.palette.background.paper,
    borderRadius: 8,
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    zIndex: 1000,
  },
  chatHeader: {
    padding: theme.spacing(1),
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.text.primary,
    textAlign: 'center',
    fontFamily: "'Ancizar Serif', serif",
  },
  messages: {
    padding: theme.spacing(2),
    maxHeight: 200,
    overflowY: 'auto',
  },
  message: {
    marginBottom: theme.spacing(1),
    padding: theme.spacing(1),
    backgroundColor: theme.palette.grey[200],
    borderRadius: 4,
  },
  inputContainer: {
    padding: theme.spacing(1),
    display: 'flex',
    alignItems: 'center',
  },
  input: {
    flexGrow: 1,
    marginRight: theme.spacing(1),
  },
}));

export default function SupportChat() {
  const classes = useStyles();
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user', time: new Date().toLocaleTimeString() }]);
      setInput('');
      // Тут можна додати логіку відправки на сервер або відповідь бота
      setTimeout(() => {
        setMessages(prev => [...prev, { text: 'Дякуємо за звернення! Ми скоро відповімо.', sender: 'support', time: new Date().toLocaleTimeString() }]);
      }, 1000);
    }
  };

  return (
    <Paper className={classes.chatContainer}>
      <Typography variant="h6" className={classes.chatHeader}>
        Технічна підтримка
      </Typography>
      <div className={classes.messages}>
        {messages.map((msg, index) => (
          <div key={index} className={classes.message}>
            <Typography variant="body2">{msg.text}</Typography>
            <Typography variant="caption" color="textSecondary">{msg.sender === 'user' ? 'Ви' : 'Підтримка'} - {msg.time}</Typography>
          </div>
        ))}
      </div>
      <div className={classes.inputContainer}>
        <TextField
          className={classes.input}
          variant="outlined"
          size="small"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
          placeholder="Напишіть повідомлення..."
        />
        <IconButton color="primary" onClick={handleSend} disabled={!input.trim()}>
          <SendIcon />
        </IconButton>
      </div>
    </Paper>
  );
}
