import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import { Link, Outlet, useLocation } from 'react-router-dom';
import Toolbar from '@material-ui/core/Toolbar';
import Container from '@material-ui/core/Container';
import IconButton from '@material-ui/core/IconButton';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';

import CartIcon from './CartIcon';
import { CartContext } from '../services/context';
import LoginDialog from '../LoginForm';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundImage: `url(/icons/newBG.jpg)`,
    backgroundSize: 'cover',
    backgroundAttachment: 'fixed',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
  },
  label: {
    width: '20%',
    fontSize: '1.8em',
    lineHeight: `48px`,
    marginLeft: '25px',
    [theme.breakpoints.down('sm')]: {
      display: 'none',
    },
  },
  tabs: {
    width: '100%',
  },
  toolbar: {
    padding: '10px 0px',
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
  },
  footer: {
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.text.primary,
    padding: theme.spacing(4, 0),
    marginTop: 'auto',
    boxShadow: `0 0 10px ${theme.palette.primary.main}55`,
  },
  footerContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
  },
}));

export default function MainLayout() {
  const classes = useStyles();
  const location = useLocation();
  const cart = useContext(CartContext);
  const [openLogin, setOpenLogin] = useState(false);

  const handleLoginOpen = () => {
    setOpenLogin(true);
  };

  const handleLoginClose = () => {
    setOpenLogin(false);
  };

  const getTabIndex = () => {
    switch (location.pathname) {
      case '/':
        return 0;
      case '/catalog':
        return 1;
      case '/about':
        return 2;
      default:
        return false;
    }
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="primary">
        <Toolbar className={classes.toolbar}>
          <Typography className={classes.label}>Мій магазин</Typography>
          <Tabs
            value={getTabIndex()}
            className={classes.tabs}
            textColor="inherit"
            indicatorColor="secondary"
          >
            <Tab label="Головна" component={Link} to="/" />
            <Tab label="Каталог" component={Link} to="/catalog" />
            <Tab label="Про нас" component={Link} to="/about" />
          </Tabs>

          <CartIcon count={cart.items.length} />

          <IconButton
            edge="end"
            color="inherit"
            onClick={handleLoginOpen}
            style={{ marginLeft: '12px' }}
          >
            <AccountCircleIcon fontSize="large" />
          </IconButton>
        </Toolbar>
      </AppBar>

      <Container>
        <Outlet />
      </Container>

      <footer className={classes.footer}>
        <Container className={classes.footerContainer}>
          <Typography variant="body1">© 2025 Мій магазин</Typography>
          <Typography variant="body1">Контакти: support@example.com</Typography>
        </Container>
      </footer>

      <LoginDialog open={openLogin} onClose={handleLoginClose} />
    </div>
  );
}
