import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, database } from '../services/firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { ref, onValue } from 'firebase/database';
import { 
  Button, 
  Typography, 
  Container, 
  Paper, 
  makeStyles, 
  Box,
  Avatar,
} from '@material-ui/core';
import OrdersTable from '../OrdersTable';
import ReviewsTable from './ReviewsTable';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.grey[900],
    minHeight: '100vh',
    padding: theme.spacing(2, 0),
    color: theme.palette.text.primary,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  header: {
    fontFamily: "'Lavishly Yours', cursive",
    fontSize: '3.5rem',
    color: 'white',
    textShadow: '0 0 10px rgba(255, 255, 255, 0.3)',
    textAlign: 'center',
    marginBottom: theme.spacing(2),
  },
  subheader: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.5rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
    marginBottom: theme.spacing(4),
  },
  adminCard: {
    backgroundColor: theme.palette.grey[800],
    padding: theme.spacing(2),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '95vw',
    marginBottom: theme.spacing(4),
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  adminText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.4rem',
    color: theme.palette.text.primary,
  },
  logoutButton: {
    fontFamily: "'Ancizar Serif', serif",
  },
  contentSection: {
    backgroundColor: theme.palette.grey[800],
    padding: theme.spacing(2),
    borderRadius: '12px',
    boxShadow: `0 0 15px ${theme.palette.primary.main}55`,
    width: '100%',
    maxWidth: '95vw',
    marginBottom: theme.spacing(4),
  },
  table: {
    minWidth: '100%',
  },
  tableHead: {
    backgroundColor: theme.palette.primary.main,
    '& th': {
      color: theme.palette.common.white,
      fontFamily: "'Ancizar Serif', serif",
      fontWeight: 'bold',
      fontSize: '1.0rem',
      padding: theme.spacing(1),
    },
  },
  tableRow: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.action.hover,
    },
    height: '60px',
  },
  tableCell: {
    fontFamily: "'Ancizar Serif', serif",
    color: theme.palette.text.primary,
    fontSize: '0.95rem',
    padding: theme.spacing(1),
    whiteSpace: 'normal',
    wordBreak: 'break-word',
    maxWidth: '150px',
  },
  noOrdersText: {
    fontFamily: "'Ancizar Serif', serif",
    fontSize: '1.4rem',
    color: theme.palette.text.secondary,
    textAlign: 'center',
    padding: theme.spacing(3),
  },
  deleteButton: {
    color: theme.palette.error.main,
  },
  editButton: {
    color: theme.palette.warning.main,
  },
  tableContainer: {
    overflowX: 'hidden',
  },
  dialogContent: {
    display: 'flex',
    flexDirection: 'column',
    gap: theme.spacing(2),
  },
  adminAvatar: {
    width: theme.spacing(12),
    height: theme.spacing(12),
    marginRight: theme.spacing(2),
  },
  pagination: {
    marginTop: theme.spacing(2),
    display: 'flex',
    justifyContent: 'center',
    gap: theme.spacing(1),
  },
  pageButton: {
    fontFamily: "'Ancizar Serif', serif",
    minWidth: '40px',
    height: '40px',
    borderRadius: '50%',
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.common.white,
    '&:hover': {
      backgroundColor: theme.palette.primary.dark,
    },
  },
  activePageButton: {
    backgroundColor: theme.palette.secondary.main,
    color: theme.palette.common.white,
    '&:hover': {
      backgroundColor: theme.palette.secondary.dark,
    },
  },
}));

export default function AdminPage() {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [currentOrderPage, setCurrentOrderPage] = useState(1);
  const [currentReviewPage, setCurrentReviewPage] = useState(1);
  const itemsPerPage = 10;
  const navigate = useNavigate();
  const classes = useStyles();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        navigate('/');
      }
    });
    return () => unsubscribe();
  }, [navigate]);

  useEffect(() => {
    const ordersRef = ref(database, 'orders');
    const unsubscribeOrders = onValue(ordersRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const ordersList = Object.entries(data).map(([id, order]) => {
          const firstItem = Array.isArray(order.items) && order.items.length > 0 ? order.items[0] : {};
          return {
            id,
            name: firstItem.name || order.name || 'Невідомо',
            price: firstItem.price || order.price || 0,
            quantity: firstItem.quantity || order.quantity || 0,
            orderDate: order.orderDate || new Date().toISOString(),
            totalPrice: order.totalPrice || 0,
            fullName: order.user?.fullName || 'Невідомо',
            phone: order.user?.phone || 'Невідомо',
            email: order.user?.email || 'Невідомо',
            deliveryMethod: order.user?.deliveryMethod || 'Невідомо',
            address: `${order.user?.address || ''}, ${order.user?.city || ''}, ${order.user?.district || ''}, ${order.user?.country || ''}`
              .trim()
              .replace(/, ,/g, ',')
              .replace(/^,|,$/g, '') || 'Невідомо',
            contactInfo: `${order.user?.fullName || 'Невідомо'} (${order.user?.email || 'Невідомо'})`,
            deliveryAndAddress: `${order.user?.deliveryMethod || 'Невідомо'}: ${order.user?.address || ''}, ${order.user?.city || ''}, ${order.user?.district || ''}, ${order.user?.country || ''}`
              .trim()
              .replace(/, ,/g, ',')
              .replace(/^,|,$/g, '') || 'Невідомо',
          };
        });
        setOrders(ordersList);
      } else {
        setOrders([]);
      }
    });

    const reviewsRef = ref(database, 'reviews');
    const unsubscribeReviews = onValue(reviewsRef, (snapshot) => {
      const data = snapshot.val();
      if (data) {
        const reviewsList = Object.entries(data).map(([id, review]) => ({
          id,
          text: review.text || 'Невідомо',
          rating: review.rating || 0,
          userName: review.userName || review.fullName || 'Невідомо',
        }));
        setReviews(reviewsList);
      } else {
        setReviews([]);
      }
    });

    return () => {
      unsubscribeOrders();
      unsubscribeReviews();
    };
  }, []);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/');
    } catch (error) {
      console.error('Помилка виходу:', error.message);
    }
  };

  if (!user) {
    return <div style={{ color: 'white', textAlign: 'center', padding: '20px' }}>Завантаження...</div>;
  }

  return (
    <div className={classes.root}>
      <Container maxWidth="xl">
        <Typography variant="h1" className={classes.header}>
          Панель адміністратора
        </Typography>
        <Typography className={classes.subheader}>
          Вітаємо, {user.email}!
        </Typography>

        <Paper className={classes.adminCard}>
          <Box display="flex" alignItems="center">
            <Avatar
              alt="Admin Photo"
              src="../icons/admin.jpg"
              className={classes.adminAvatar}
            />
            <Typography className={classes.adminText}>
              Ви увійшли як: <strong>{user.email}</strong>
            </Typography>
          </Box>
          <Button
            variant="contained"
            color="secondary"
            onClick={handleLogout}
            className={classes.logoutButton}
          >
            Вийти
          </Button>
        </Paper>

        <OrdersTable
          classes={classes}
          orders={orders}
          setOrders={setOrders}
          itemsPerPage={itemsPerPage}
          currentPage={currentOrderPage}
          setCurrentPage={setCurrentOrderPage}
        />

        <ReviewsTable
          classes={classes}
          reviews={reviews}
          setReviews={setReviews}
          itemsPerPage={itemsPerPage}
          currentPage={currentReviewPage}
          setCurrentPage={setCurrentReviewPage}
        />
      </Container>
    </div>
  );
}
