import React from 'react';

const sneakersData = [
  {
    id: 1,
    text: "Легка вага та комфорт. Ідеальні кросівки для активного дня.",
    img: "/SneakerSection/1.jpg",  // так підключаємо з public
  },
  {
    id: 2,
    text: "Сучасний дизайн. Стильні, щоб виділятися.",
    img: "https://unsplash.com/photos/a-close-up-of-a-black-shoe-with-yellow-stitchs-uYPHPFkUVMo",
  },
  {
    id: 3,
    text: "Дихаючі матеріали забезпечують свіжість ніг.",
    img: "https://unsplash.com/photos/a-close-up-of-a-shoe-on-a-table-P8V7AFmYdyw",
  },
];

const SneakerSection = () => {
  return (
    <div>
      {sneakersData.map((item, index) => (
        <div
          key={item.id}
          style={{
            display: "flex",
            flexDirection: index % 2 === 0 ? "row" : "row-reverse",
            alignItems: "center",
            marginBottom: "40px",
          }}
        >
          <div style={{ flex: 1, padding: "20px" }}>
            <p style={{ fontSize: "18px", lineHeight: "1.4" }}>{item.text}</p>
          </div>
          <div style={{ flex: 1, padding: "20px" }}>
            <img
              src={item.img}
              alt="Sneaker"
              style={{ width: "100%", borderRadius: "8px" }}
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default SneakerSection;
