import React, { useState, useEffect } from 'react';
import { Box, Typography, Fade, useTheme } from '@mui/material';

const advices = [
  {
    quote: "–°—Ç–∏–ª—å ‚Äî —Ü–µ —Å–ø–æ—Å—ñ–± —Å–∫–∞–∑–∞—Ç–∏, —Ö—Ç–æ —Ç–∏ —î, –Ω–µ –≤–∏–º–æ–≤–∏–≤—à–∏ –∂–æ–¥–Ω–æ–≥–æ —Å–ª–æ–≤–∞.",
    author: "Rachel Zoe",
    tip: "–ó–∞–≤–∂–¥–∏ –ø–µ—Ä–µ–≤—ñ—Ä—è–π –º–∞—Ç–µ—Ä—ñ–∞–ª —ñ —Ç–∏–ø –ø—ñ–¥–æ—à–≤–∏ ‚Äî –≤–æ–Ω–∏ –≤—ñ–¥–ø–æ–≤—ñ–¥–∞—é—Ç—å –∑–∞ –∫–æ–º—Ñ–æ—Ä—Ç —ñ –¥–æ–≤–≥–æ–≤—ñ—á–Ω—ñ—Å—Ç—å."
  },
  {
    quote: "–•–æ—Ä–æ—à–µ –≤–∑—É—Ç—Ç—è –≤–µ–¥–µ —Ç–µ–±–µ –¥–æ —Ö–æ—Ä–æ—à–∏—Ö –º—ñ—Å—Ü—å.",
    tip: "–û–±–∏—Ä–∞–π —Ä–æ–∑–º—ñ—Ä –∑—ñ –∑—Ä—É—á–Ω–æ—é –ø–æ—Å–∞–¥–∫–æ—é ‚Äî –Ω–µ —Ç–∏—Å–Ω–µ —ñ –Ω–µ –±–æ–≤—Ç–∞—î—Ç—å—Å—è."
  },
  {
    quote: "–í–∞—à –æ–±—Ä–∞–∑ –ø–æ—á–∏–Ω–∞—î—Ç—å—Å—è –∑–Ω–∏–∑—É ‚Äî –∑ –≤–∑—É—Ç—Ç—è.",
    tip: "–û–±–∏—Ä–∞–π –ø–∞—Ä—É –¥–ª—è –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–≥–æ —Å—Ç–∏–ª—é ‚Äî —Å–ø–æ—Ä—Ç, –∫–µ–∂—É–∞–ª —á–∏ –∫–ª–∞—Å–∏–∫–∞."
  },
  {
    quote: "–í–∑—É—Ç—Ç—è –≥–æ–≤–æ—Ä–∏—Ç—å —â–µ –¥–æ —Ç–æ–≥–æ, —è–∫ —Ç–∏ –≤—ñ–¥–∫—Ä–∏—î—à —Ä–æ—Ç–∞.",
    tip: "–ù–µ –∫—É–ø—É–π —Ç—ñ–ª—å–∫–∏ —á–µ—Ä–µ–∑ –±—Ä–µ–Ω–¥ ‚Äî –∑–≤–µ—Ä–Ω–∏ —É–≤–∞–≥—É –Ω–∞ —Ñ—É–Ω–∫—Ü—ñ–æ–Ω–∞–ª—å–Ω—ñ—Å—Ç—å."
  }
];

const AdviceSection = () => {
  const theme = useTheme();
  const [index, setIndex] = useState(0);
  const [fadeIn, setFadeIn] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setFadeIn(false);
      setTimeout(() => {
        setIndex((prev) => (prev + 1) % advices.length);
        setFadeIn(true);
      }, 500); // Fade-out delay
    }, 6000);
    return () => clearInterval(interval);
  }, []);

  const { quote, author, tip } = advices[index];

  return (
    <Box
      sx={{
        bgcolor: 'rgba(0,0,0,0.6)',
        backdropFilter: 'blur(6px)',
        borderRadius: 3,
        px: 4,
        py: 3,
        textAlign: 'center',
        color: 'white',
        my: 4,
        mx: 'auto',
        maxWidth: 800,
      }}
    >
      <Fade in={fadeIn} timeout={500}>
        <Box>
          <Typography variant="h6" fontStyle="italic">
            ‚Äú{quote}‚Äù
          </Typography>
          {author && (
            <Typography variant="body2" sx={{ mt: 1, opacity: 0.6 }}>
              ‚Äî {author}
            </Typography>
          )}
          <Typography variant="body1" sx={{ mt: 2 }}>
            üëü {tip}
          </Typography>
        </Box>
      </Fade>
    </Box>
  );
};

export default AdviceSection;
