// services/currencyService.js
const API_KEY = 'YOUR_API_KEY'; // Замініть на ваш API-ключ
const BASE_URL = 'https://api.exchangerate-api.com/v4/latest/';

export const getExchangeRate = async (baseCurrency = 'USD') => {
  try {
    const response = await fetch(`${BASE_URL}${baseCurrency}?access_key=${API_KEY}`);
    const data = await response.json();
    if (!data.rates) {
      throw new Error('Failed to fetch exchange rates');
    }
    return data.rates;
  } catch (error) {
    console.error('Error fetching exchange rates:', error);
    return null;
  }
};

export const convertCurrency = async (amount, fromCurrency = 'USD', toCurrency = 'UAH') => {
  const rates = await getExchangeRate(fromCurrency);
  if (!rates || !rates[toCurrency]) {
    throw new Error(`Currency ${toCurrency} not supported`);
  }
  return (amount * rates[toCurrency]).toFixed(2);
};
